// DEBUG: Log the raw input
const rawInput = $input.first();
const rawJson = rawInput ? rawInput.json : null;

// Try multiple possible field names
const stdout = rawJson?.stdout || rawJson?.output || rawJson?.data || '';

// DEBUG: Return debug info if no stdout found
if (!stdout || stdout.trim() === 'NO_FAILURES' || !stdout.trim()) {
  return [{ json: { 
    no_failures: true,
    _debug_raw_keys: rawJson ? Object.keys(rawJson) : [],
    _debug_raw_json: JSON.stringify(rawJson).substring(0, 500),
    _debug_stdout_value: stdout
  }}];
}

// Parse failed containers
const lines = stdout.split('\n').filter(l => l.trim());
const results = [];

for (const line of lines) {
  const parts = line.split('|');
  if (parts.length >= 3) {
    const name = parts[0].trim();
    const status = parts[1].trim();
    const state = parts[2].trim();

    const exitMatch = status.match(/Exited \((\d+)\)/);
    const exitCode = exitMatch ? exitMatch[1] : 'unknown';

    results.push({
      json: {
        container_name: name,
        status: status,
        state: state,
        exit_code: exitCode,
        timestamp: new Date().toISOString()
      }
    });
  }
}

// DEBUG: If no results parsed, show what we got
if (results.length === 0) {
  return [{ json: { 
    no_failures: true,
    _debug_lines_found: lines.length,
    _debug_first_line: lines[0] || 'none',
    _debug_stdout: stdout.substring(0, 500)
  }}];
}

return results;

const container = $('Parse Failures').first().json;
const logs = $input.first().json.stdout || 'No logs available';

const truncatedLogs = logs.length > 2000 
  ? logs.substring(logs.length - 2000) 
  : logs;

const task = `Analyze this Docker container failure and determine the root cause.

## Container Details
- **Name**: ${container.container_name}
- **Status**: ${container.status}
- **Exit Code**: ${container.exit_code}
- **Detected At**: ${container.timestamp}

## Recent Logs (last 50 lines)
\`\`\`
${truncatedLogs}
\`\`\`

## Available Tools
You have SSH access to the home server. Use these tools to investigate:
- docker_ps() - List all containers and their status
- docker_logs(container, tail) - Get more logs from a container
- docker_inspect(container) - Get container details (health, restart count, etc.)
- docker_restart(container) - Restart a container (only if confident it will help)
- run_on_server(command) - Run any command on the server via SSH
- search_skills(query) - Find troubleshooting guides

## Instructions
1. Analyze the provided logs for obvious errors
2. If needed, use docker_inspect() to check health status and restart count
3. If needed, use docker_logs() to get more context
4. Search for relevant troubleshooting skills if the error is unclear
5. Provide your final_answer with:
   - Root cause of the failure
   - Recommended fix
   - Severity (critical/warning/info)
   - Whether the container was auto-restarted (if applicable)`;

return [{
  json: {
    task: task,
    working_directory: '/tmp',
    model: 'auto',
    max_steps: 15,
    source: 'n8n',
    triggered_by: 'container-crash-monitor',
    container_name: container.container_name,
    exit_code: container.exit_code
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'success',
    run_id: agentResult.run_id || null,
    container_name: taskData.container_name,
    exit_code: taskData.exit_code,
    agent_analysis: agentResult.final_answer || 'No analysis provided',
    steps_taken: agentResult.total_steps || 0,
    timestamp: new Date().toISOString()
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'error',
    container_name: taskData.container_name,
    exit_code: taskData.exit_code,
    error: agentResult.error || agentResult.terminated_reason || 'Unknown error',
    steps_taken: agentResult.total_steps || 0,
    timestamp: new Date().toISOString()
  }
}];
