const stdout = $input.first().json.stdout || '';

// If no failures, return marker
if (stdout.trim() === 'NO_FAILURES' || !stdout.trim()) {
  return [{ json: { no_failures: true } }];
}

// Parse failed containers
const lines = stdout.split('\n').filter(l => l.trim());
const results = [];

for (const line of lines) {
  const parts = line.split('|');
  if (parts.length >= 3) {
    const name = parts[0].trim();
    const status = parts[1].trim();
    const state = parts[2].trim();

    // Extract exit code if present
    const exitMatch = status.match(/Exited \((\d+)\)/);
    const exitCode = exitMatch ? exitMatch[1] : 'unknown';

    results.push({
      json: {
        container_name: name,
        status: status,
        state: state,
        exit_code: exitCode,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results.length > 0 ? results : [{ json: { no_failures: true } }];

const container = $('Parse Failures').first().json;
const logs = $input.first().json.stdout || 'No logs available';

// Truncate logs if too long
const truncatedLogs = logs.length > 2000 
  ? logs.substring(logs.length - 2000) 
  : logs;

const task = `A Docker container has crashed and needs analysis.

## Container Details
- **Name**: ${container.container_name}
- **Status**: ${container.status}
- **Exit Code**: ${container.exit_code}
- **Detected At**: ${container.timestamp}

## Recent Logs (last 50 lines)
\`\`\`
${truncatedLogs}
\`\`\`

## Your Task
1. Analyze the logs to identify the root cause of the crash
2. Search for relevant troubleshooting skills using search_skills()
3. Provide a summary of:
   - What caused the crash
   - Recommended fix
   - Whether this is a recurring issue or one-time failure
4. Do NOT restart the container automatically - just analyze and report`;

return [{
  json: {
    task: task,
    working_directory: '/tmp',
    model: 'auto',
    max_steps: 15,
    container_name: container.container_name,
    exit_code: container.exit_code
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'success',
    container_name: taskData.container_name,
    exit_code: taskData.exit_code,
    agent_analysis: agentResult.final_answer || 'No analysis provided',
    steps_taken: agentResult.total_steps || 0,
    timestamp: new Date().toISOString()
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'error',
    container_name: taskData.container_name,
    exit_code: taskData.exit_code,
    error: agentResult.error || agentResult.terminated_reason || 'Unknown error',
    steps_taken: agentResult.total_steps || 0,
    timestamp: new Date().toISOString()
  }
}];
