// Get raw input
const rawInput = $input.first();
const rawJson = rawInput ? rawInput.json : null;
const stdout = rawJson?.stdout || rawJson?.output || rawJson?.data || '';

// Check for no failures - return empty array to stop workflow
if (!stdout || stdout.trim() === 'NO_FAILURES' || !stdout.trim()) {
  return [];
}

// Parse failed containers
const lines = stdout.split('\n').filter(l => l.trim());
const results = [];

for (const line of lines) {
  const parts = line.split('|');
  if (parts.length >= 3) {
    const name = parts[0].trim();
    const status = parts[1].trim();
    const state = parts[2].trim();

    const exitMatch = status.match(/Exited \((\d+)\)/);
    const exitCode = exitMatch ? exitMatch[1] : null;

    results.push({
      json: {
        container_name: name,
        status: status,
        state: state,
        exit_code: exitCode,
        timestamp: new Date().toISOString()
      }
    });
  }
}

return results;

// Use $itemIndex to get the correct container
const allContainers = $('Parse Failures').all();
const container = allContainers[$itemIndex]?.json || allContainers[0]?.json;
const logs = $input.first().json.stdout || 'No logs available';

const truncatedLogs = logs.length > 2000 
  ? logs.substring(logs.length - 2000) 
  : logs;

// Determine severity
let severity = 'medium';
const exitCode = container?.exit_code;
const status = container?.status || '';
if (exitCode === '137' || status.includes('unhealthy')) {
  severity = 'high';
} else if (exitCode === '1' || exitCode === '2') {
  severity = 'high';
} else if (status.includes('Restarting')) {
  severity = 'high';
}

const task = `Analyze this Docker container failure and determine the root cause.

## Container Details
- **Name**: ${container?.container_name || 'unknown'}
- **Status**: ${container?.status || 'unknown'}
- **State**: ${container?.state || 'unknown'}
- **Exit Code**: ${container?.exit_code || 'N/A'}
- **Severity**: ${severity}
- **Detected At**: ${container?.timestamp || new Date().toISOString()}

## Recent Logs (last 50 lines)
\`\`\`
${truncatedLogs}
\`\`\`

## Instructions
1. Analyze the provided logs for obvious errors
2. Use search_skills('docker') or search_skills('troubleshoot') to find relevant guides
3. If needed, use run_on_server() for docker commands (docker is NOT installed locally - only available on the host server)
4. Determine the root cause and recommended fix
5. Call task_complete with your analysis including:
   - Root cause of the failure
   - Recommended fix
   - Severity assessment (critical/warning/info)
   - Whether auto-restart is recommended`;

return [{
  json: {
    task: task,
    working_directory: '/tmp',
    model: 'auto',
    max_steps: 15,
    source: 'n8n',
    triggered_by: 'container-crash-monitor',
    _container_name: container?.container_name || 'unknown',
    _exit_code: container?.exit_code || null,
    _severity: severity
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'success',
    run_id: agentResult.run_id || null,
    container_name: taskData._container_name,
    exit_code: taskData._exit_code,
    severity: taskData._severity,
    agent_analysis: agentResult.final_answer || 'No analysis provided',
    steps_taken: agentResult.total_steps || 0,
    terminated_reason: agentResult.terminated_reason || 'unknown',
    timestamp: new Date().toISOString()
  }
}];

const agentResult = $input.first().json;
const taskData = $('Build Agent Task').first().json;

return [{
  json: {
    status: 'error',
    run_id: agentResult.run_id || null,
    container_name: taskData._container_name,
    exit_code: taskData._exit_code,
    severity: taskData._severity,
    error: agentResult.terminated_reason || agentResult.error || 'Unknown error',
    steps_taken: agentResult.total_steps || 0,
    timestamp: new Date().toISOString()
  }
}];
