// Parse container status and filter for exit code 128
const items = $input.all();
const results = [];

// Map container names to docker-compose service names
const containerToService = {
  'media-download-nzbget': 'nzbget',
  'media-download-qbittorrent': 'qbittorrent',
  'media-download-slskd': 'slskd'
};

for (const item of items) {
  const stdout = item.json.stdout || '';
  const lines = stdout.split('\n').filter(l => l.trim());

  for (const line of lines) {
    const parts = line.split('|');
    if (parts.length >= 3) {
      const containerName = parts[0].trim();
      const status = parts[1].trim();
      const state = parts[2].trim();

      // Extract exit code if present
      const exitCodeMatch = status.match(/Exited \((\d+)\)/);
      const exitCode = exitCodeMatch ? exitCodeMatch[1] : null;

      // Only process containers that exited with code 128 (network namespace loss)
      // and have a corresponding service name
      if (exitCode === '128' && containerToService[containerName]) {
        results.push({
          container_name: containerName,
          service_name: containerToService[containerName],
          status: status,
          state: state,
          exit_code: exitCode,
          timestamp: new Date().toISOString()
        });
      }
    }
  }
}

return results.map(r => ({ json: r }));

// Format result message
const containerName = $('Parse and Filter Containers').first().json.container_name;
const serviceName = $('Parse and Filter Containers').first().json.service_name;
const restartOutput = $('Restart Container').first().json.stdout || '';
const verifyStatus = $('Verify Restart').first().json.stdout || '';

const isRunning = verifyStatus.includes('Up');
const timestamp = new Date().toISOString();

return [{
  json: {
    container_name: containerName,
    service_name: serviceName,
    restart_successful: isRunning,
    restart_output: restartOutput,
    verification_status: verifyStatus,
    timestamp: timestamp,
    message: isRunning 
      ? `✅ Successfully restarted ${containerName} (${serviceName})`
      : `⚠️ Restart attempted for ${containerName} but verification failed`
  }
}];
