// Extract message and user info
const message = $input.first().json.message;
const chatId = message.chat.id;
const userId = message.from.id;
const username = message.from.username || message.from.first_name || 'Unknown';
const text = message.text || '';

// Check if user is authorized (you can add your Telegram user ID here)
const authorizedUsers = [
  // Add your Telegram user ID here
  // Example: 123456789
];

const isAuthorized = authorizedUsers.length === 0 || authorizedUsers.includes(userId);

return [{
  json: {
    chatId: chatId,
    userId: userId,
    username: username,
    message: text,
    isAuthorized: isAuthorized,
    originalMessage: message
  }
}];

// Parse OpenAI response and check if it wants to execute a command
const openaiResponse = $('OpenAI Chat').first().json;
const messageData = $('Parse Message').first().json;

let responseText = '';
let commandToExecute = null;
let explanation = '';

try {
  // Get the message content from OpenAI response
  const content = openaiResponse.choices?.[0]?.message?.content || '';

  // Try to parse JSON if OpenAI returned structured response
  try {
    const parsed = JSON.parse(content);
    if (parsed.action === 'execute' && parsed.command) {
      commandToExecute = parsed.command;
      explanation = parsed.explanation || 'Executing command';
      responseText = `ü§ñ ${explanation}\n\nExecuting: \`${commandToExecute}\``;
    } else {
      responseText = content;
    }
  } catch (e) {
    // Not JSON, just use the text response
    responseText = content;
  }

  // Check for dangerous commands
  const dangerousPatterns = [
    /rm\s+-rf/i,
    /format/i,
    /dd\s+if=/i,
    /mkfs/i,
    /fdisk/i
  ];

  if (commandToExecute) {
    for (const pattern of dangerousPatterns) {
      if (pattern.test(commandToExecute)) {
        commandToExecute = null;
        responseText = '‚ùå Blocked: This command is too dangerous to execute.';
        break;
      }
    }
  }

} catch (error) {
  responseText = 'Error processing request: ' + error.message;
}

return [{
  json: {
    chatId: messageData.chatId,
    responseText: responseText,
    commandToExecute: commandToExecute,
    explanation: explanation,
    originalMessage: messageData.message
  }
}];

// Format command execution result
const commandData = $('Parse OpenAI Response').first().json;
const execResult = $('Execute Command').first().json;

const stdout = execResult.stdout || '';
const stderr = execResult.stderr || '';
const exitCode = execResult.exitCode || 0;

let resultText = '';
if (stdout) {
  resultText += `‚úÖ Output:\n\`\`\`\n${stdout.substring(0, 3000)}\n\`\`\``;
}
if (stderr) {
  resultText += `\n\n‚ö†Ô∏è Errors:\n\`\`\`\n${stderr.substring(0, 1000)}\n\`\`\``;
}
if (exitCode !== 0) {
  resultText = `‚ùå Command failed (exit code: ${exitCode})\n\n${resultText}`;
}

if (!resultText) {
  resultText = '‚úÖ Command executed successfully (no output)';
}

return [{
  json: {
    chatId: commandData.chatId,
    responseText: resultText,
    originalCommand: commandData.commandToExecute
  }
}];
