#!/bin/bash
# Setup auto-login and auto-launch Grafana dashboard in fullscreen
# For Debian 12 with GNOME/GDM3

set -e

echo "🔧 Setting up auto-login and Grafana auto-launch..."
echo ""

# Check if running as root
if [ "$EUID" -ne 0 ]; then 
    echo "❌ Please run as root (use sudo)"
    exit 1
fi

USERNAME="unarmedpuppy"
GRAFANA_URL="http://192.168.86.47:3010/"

# Detect browser
if command -v chromium &> /dev/null; then
    BROWSER="chromium"
    BROWSER_CMD="chromium --kiosk --app=$GRAFANA_URL"
elif command -v chromium-browser &> /dev/null; then
    BROWSER="chromium-browser"
    BROWSER_CMD="chromium-browser --kiosk --app=$GRAFANA_URL"
elif command -v firefox &> /dev/null; then
    BROWSER="firefox"
    BROWSER_CMD="firefox -kiosk $GRAFANA_URL"
elif command -v google-chrome &> /dev/null; then
    BROWSER="google-chrome"
    BROWSER_CMD="google-chrome --kiosk --app=$GRAFANA_URL"
else
    echo "❌ No browser found. Installing Chromium..."
    apt-get update
    apt-get install -y chromium
    BROWSER="chromium"
    BROWSER_CMD="chromium --kiosk --app=$GRAFANA_URL"
fi

echo "✅ Using browser: $BROWSER"
echo ""

# 1. Configure GDM3 for auto-login
echo "📝 Configuring GDM3 for auto-login..."

GDM_CUSTOM_CONF="/etc/gdm3/custom.conf"

# Backup existing config
if [ -f "$GDM_CUSTOM_CONF" ]; then
    cp "$GDM_CUSTOM_CONF" "${GDM_CUSTOM_CONF}.bak.$(date +%Y%m%d_%H%M%S)"
fi

# Create or update custom.conf
if [ ! -f "$GDM_CUSTOM_CONF" ]; then
    touch "$GDM_CUSTOM_CONF"
fi

# Remove existing [daemon] section and add new one
sed -i '/^\[daemon\]/,/^\[/ { /^\[daemon\]/!{ /^\[/!d; }; }' "$GDM_CUSTOM_CONF" 2>/dev/null || true

# Add [daemon] section with auto-login at the beginning
if ! grep -q "^\[daemon\]" "$GDM_CUSTOM_CONF"; then
    sed -i "1i[daemon]\nAutomaticLogin=$USERNAME\nAutomaticLoginEnable=true\n" "$GDM_CUSTOM_CONF"
else
    # Update existing [daemon] section
    sed -i '/^\[daemon\]/,/^\[/ {
        /^\[daemon\]/a\
AutomaticLogin='"$USERNAME"'\
AutomaticLoginEnable=true
        /^AutomaticLogin=/d
        /^AutomaticLoginEnable=/d
    }' "$GDM_CUSTOM_CONF"
fi

echo "✅ GDM3 configured for auto-login"
echo ""

# 2. Create autostart desktop entry for Grafana
echo "📝 Creating autostart entry for Grafana..."

AUTOSTART_DIR="/home/$USERNAME/.config/autostart"
mkdir -p "$AUTOSTART_DIR"

# Create desktop entry
cat > "$AUTOSTART_DIR/grafana-dashboard.desktop" << EOF
[Desktop Entry]
Type=Application
Name=Grafana Dashboard
Comment=Auto-launch Grafana dashboard in fullscreen
Exec=$BROWSER_CMD
Icon=grafana
Terminal=false
Categories=Network;Monitoring;
X-GNOME-Autostart-enabled=true
StartupNotify=false
NoDisplay=false
EOF

# Set ownership
chown -R "$USERNAME:$USERNAME" "$AUTOSTART_DIR"
chmod +x "$AUTOSTART_DIR/grafana-dashboard.desktop"

echo "✅ Autostart entry created: $AUTOSTART_DIR/grafana-dashboard.desktop"
echo ""

# 3. Create .xprofile startup script (most reliable)
echo "📝 Creating .xprofile startup script..."

STARTUP_SCRIPT="/home/$USERNAME/.xprofile"
cat > "$STARTUP_SCRIPT" << SCRIPT_EOF
#!/bin/bash
# Auto-launch Grafana dashboard on login

# Wait for display and desktop to be ready
sleep 5

# Detect browser and launch
if command -v chromium &> /dev/null; then
    chromium --kiosk --app=$GRAFANA_URL &
elif command -v chromium-browser &> /dev/null; then
    chromium-browser --kiosk --app=$GRAFANA_URL &
elif command -v firefox &> /dev/null; then
    firefox -kiosk $GRAFANA_URL &
elif command -v google-chrome &> /dev/null; then
    google-chrome --kiosk --app=$GRAFANA_URL &
fi
SCRIPT_EOF

chown "$USERNAME:$USERNAME" "$STARTUP_SCRIPT"
chmod +x "$STARTUP_SCRIPT"

echo "✅ Startup script created: $STARTUP_SCRIPT"
echo ""

